// Analytics de la vectorisation
const inputItems = $input.all();
const totalDocuments = inputItems.length;

// Calculer les statistiques
const stats = {
  total_documents: totalDocuments,
  timestamp: new Date().toISOString(),
  documents_by_type: {},
  documents_by_category: {},
  total_content_length: 0,
  average_content_length: 0
};

// Analyser chaque document
for (const item of inputItems) {
  const doc = item.json;

  // Compter par type
  const type = doc.metadata?.type || 'unknown';
  stats.documents_by_type[type] = (stats.documents_by_type[type] || 0) + 1;

  // Compter par catégorie
  const category = doc.metadata?.category || 'unknown';
  stats.documents_by_category[category] = (stats.documents_by_category[category] || 0) + 1;

  // Calculer longueur contenu
  const contentLength = doc.content?.length || 0;
  stats.total_content_length += contentLength;
}

// Calculer moyenne
stats.average_content_length = Math.round(stats.total_content_length / totalDocuments);

console.log('=== VECTORISATION BDD JAK COMPANY TERMINÉE ===');
console.log('Total documents vectorisés:', stats.total_documents);
console.log('Répartition par type:', JSON.stringify(stats.documents_by_type, null, 2));
console.log('Répartition par catégorie:', JSON.stringify(stats.documents_by_category, null, 2));
console.log('Longueur moyenne du contenu:', stats.average_content_length, 'caractères');

return [{
  json: {
    status: 'success',
    message: 'Vectorisation BDD JAK Company terminée avec succès',
    statistics: stats,
    next_steps: [
      'Tester la recherche vectorielle',
      'Intégrer le RAG tool dans l\'AI Agent',
      'Valider les résultats avec des requêtes test'
    ]
  }
}];

